package Lec_1;

public class GreatestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 189;
		int b = 134;
		int c = 95;
		if(a>b && a>c) {
			System.out.println(a+" is greatest");
		}
		else if(b>c && b>a){
			System.out.println(b+" is greatest");
		}
		else {
			System.out.println(c+" is greatest");
		  }
		}

}	

